'use strict';

/**
* Bundle benchmarks files into a single file.
*
* @module @stdlib/_tools/benchmarks/bundle
*
* @example
* var bundle = require( '@stdlib/_tools/benchmarks/bundle' );
*
* var opts = {
*     'pattern': '\*\*\/benchmark*.js',
*     'out': '/foo/bar/bundle.js'
* };
*
* var root = '/beep/boop';
*
* bundle( root, opts, clbk );
*
* function clbk( error ) {
*     if ( error ) {
*         throw error;
*     }
* }
*
* @example
* var bundle = require( '@stdlib/_tools/benchmarks/bundle' );
*
* var opts = {
*     'pattern': '\*\*\/benchmark*.js'
* };
*
* var root = '/beep/boop';
*
* bundle( root, opts, clbk );
*
* function clbk( error, bundle ) {
*     if ( error ) {
*         throw error;
*     }
*     console.log( bundle.toString() );
* }
*/

// MODULES //

var main = require( './main.js' );


// EXPORTS //

module.exports = main;
