'use strict';

/**
* Generate an HTML file for running benchmarks.
*
* @module @stdlib/_tools/benchmarks/html
*
* @example
* var build = require( '@stdlib/_tools/benchmarks/html' );
*
* var bundle = '/foo/bar/bundle.js';
* var opts = {
*     'out': '/foo/bar/benchmarks.html';
* }
*
* build( bundle, opts, clbk );
*
* function clbk( error ) {
*     if ( error ) {
*         throw error;
*     }
* }
*
* @example
* var build = require( '@stdlib/_tools/benchmarks/html' );
*
* var bundle = '/foo/bar/bundle.js';
*
* build( bundle, clbk );
*
* function clbk( error, html ) {
*     if ( error ) {
*         throw error;
*     }
*     console.log( html );
* }
*/

// MODULES //

var main = require( './main.js' );


// EXPORTS //

module.exports = main;
