'use strict';

/**
* Bundle files into a single file using `browserify`.
*
* @module @stdlib/_tools/browserify/file-list
*
* @example
* var bundle = require( '@stdlib/_tools/browserify/file-list' );
*
* var files = [ '/foo/bar.js', '/beep/boop.js' ];
* var dest = '/bip/bundle.js';
*
* bundle( files, dest, clbk );
*
* function clbk( error ) {
*     if ( error ) {
*         throw error;
*     }
* }
*
* @example
* var bundle = require( '@stdlib/_tools/browserify/file-list' );
*
* var files = [ '/foo/bar.js', '/beep/boop.js' ];
*
* bundle( files, clbk );
*
* function clbk( error, bundle ) {
*     if ( error ) {
*         throw error;
*     }
*     console.log( bundle.toString() );
* }
*/

// MODULES //

var main = require( './main.js' );


// EXPORTS //

module.exports = main;
