'use strict';

/**
* Generate a browserify bundle from an input string.
*
* @module @stdlib/_tools/browserify/string
*
* @example
* var join = require( 'path' ).join;
* var bundle = require( '@stdlib/_tools/browserify/string' );
*
* var str = 'module.exports = require( "@stdlib/_tools/browserify/string" );';
*
* var dest = join( __dirname, 'tmp', 'bundle.js' );
* bundle( str, dest, clbk );
*
* function clbk( error ) {
*     if ( error ) {
*         throw error;
*     }
* }
*
* @example
* var bundle = require( '@stdlib/_tools/browserify/string' );
*
* var str = 'module.exports = require( "@stdlib/_tools/browserify/string" );';
*
* bundle( str, clbk );
*
* function clbk( error, bundle ) {
*     if ( error ) {
*         throw error;
*     }
*     console.log( bundle.toString() );
* }
*/

// MODULES //

var main = require( './main.js' );


// EXPORTS //

module.exports = main;
