'use strict';

// MODULES //

var logger = require( 'debug' );
var browserify = require( 'browserify' );
var copy = require( '@stdlib/utils/copy' );
var isStringArray = require( '@stdlib/assert/is-string-array' ).primitives;
var isFunction = require( '@stdlib/assert/is-function' );
var isArray = require( '@stdlib/assert/is-array' );
var browserifyString = require( '@stdlib/_tools/browserify/string' );
var format = require( '@stdlib/string/format' );
var browserifyPaths = require( './browserify_paths.js' );
var validate = require( './validate.js' );
var defaults = require( './defaults.json' );
var unique = require( './unique.js' );
var setdiff = require( './setdiff.js' );
var treeFilter = require( './tree_filter.js' );
var namespaces = require( './namespaces.js' );
var toFlatNamespace = require( './to_flat.js' );
var toTreeNamespace = require( './to_tree.js' );
var fileStringRequire = require( './file_string_require.js' );


// VARIABLES //

var debug = logger( 'bundle-pkg-list:main' );


// MAIN //

/**
* Returns a custom bundle.
*
* @param {StringArray} pkgs - list of packages to bundle
* @param {Options} [options] - function options
* @param {string} [options.namespace="tree"] - specifies whether the namespace should be a "tree", "flat", or "none"
* @param {string} [options.bundler="browserify"] - bundler
* @param {string} [options.standalone] - standalone browserify bundle export name
* @param {string} [options.requireName="require"] - name of function exposed to supporting external loading of modules from a browserify bundle
* @param {string} [options.exportName="@stdlib/stdlib"] - bundle target name which is used to load a bundle from the external environment
* @param {Array} [options.transforms] - transforms to apply when bundling
* @param {Array} [options.plugins] - plugins to apply when bundling
* @param {StringArray} [options.external] - module(s) to omit from a generated bundle which can be assumed to be present in a separate external bundle
* @param {StringArray} [options.paths] - directory paths in which to search for modules referenced via non-relative paths
* @param {Callback} clbk - callback to invoke upon completion
* @throws {TypeError} first argument must be an array of strings
* @throws {TypeError} options argument must be an object
* @throws {TypeError} must provide valid options
* @throws {RangeError} must provide a recognized `namespace` option
* @throws {RangeError} must provide a recognized `bundler` option
* @throws {TypeError} last argument must be a function
* @returns {void}
*
* @example
* var pkgs = [
*     '@stdlib/math/base/special/erf',
*     '@stdlib/math/base/special/gamma'
* ];
*
* bundle( pkgs, onBundle );
*
* function onBundle( error, bundle ) {
*     if ( error ) {
*         throw error;
*     }
*     console.log( bundle.toString() );
* }
*/
function bundle( pkgs, options, clbk ) {
	var bopts;
	var opts;
	var err;
	var out;
	var cb;
	var b;
	var v;
	var i;

	if ( !isStringArray( pkgs ) ) {
		throw new TypeError( format( 'invalid argument. First argument must be an array of strings. Value: `%s`.', pkgs ) );
	}
	opts = copy( defaults );
	if ( arguments.length > 2 ) {
		cb = clbk;
		err = validate( opts, options );
		if ( err ) {
			throw err;
		}
	} else {
		cb = options;
	}
	if ( !isFunction( cb ) ) {
		throw new TypeError( format( 'invalid argument. Last argument must be a function. Value: `%s`.', cb ) );
	}
	debug( 'Options: %s', JSON.stringify( opts ) );

	// Sort the packages in lexicographic order (e.g., [ '@stdlib/math/base', '@stdlib/math' ] => [ '@stdlib/math', '@stdlib/math/base' ] ):
	pkgs.sort();

	// Remove duplicates:
	pkgs = unique( pkgs );

	// If we do not need to generate a namespace for a browserify bundle, we will generate a bundle which allows a bundle consumer to `require` each package individually...
	if ( opts.namespace === 'none' && opts.bundler === 'browserify' ) {
		bopts = {
			'externalRequireName': opts.requireName,
			'require': pkgs,
			'paths': ( opts.paths ) ? opts.paths : browserifyPaths()
		};
		debug( 'Browserify options: %s', JSON.stringify( bopts ) );
		b = browserify( bopts );
		if ( opts.transforms ) {
			for ( i = 0; i < opts.transforms.length; i++ ) {
				v = opts.transforms[ i ];
				if ( isArray( v ) ) {
					b.transform( v[ 0 ], v[ 1 ] );
				} else {
					b.transform( v );
				}
			}
		}
		if ( opts.plugins ) {
			for ( i = 0; i < opts.plugins.length; i++ ) {
				v = opts.plugins[ i ];
				if ( isArray( v ) ) {
					b.plugin( v[ 0 ], v[ 1 ] );
				} else {
					b.plugin( v );
				}
			}
		}
		if ( opts.external ) {
			b.external( opts.external );
		}
		return b.bundle( onBundle );
	}

	// Generate a namespace...
	if ( opts.namespace === 'flat' ) {
		debug( 'Generating a flat namespace...' );

		debug( 'Excluding namespace packages...' );
		pkgs = setdiff( namespaces, pkgs );

		out = toFlatNamespace( pkgs );
	} else {
		debug( 'Generating a tree namespace...' );

		debug( 'Filtering namespace packages...' );
		pkgs = treeFilter( pkgs );

		out = toTreeNamespace( pkgs );
	}
	debug( 'Finished generating a namespace.' );

	debug( 'Generating an entry file...' );
	out = fileStringRequire( out );
	debug( 'Finished generating an entry file.' );

	debug( 'Generating bundle...' );
	if ( opts.bundler === 'browserify' ) {
		bopts = {
			'requireName': opts.requireName,
			'transforms': opts.transforms,
			'plugins': opts.plugins,
			'paths': ( opts.paths ) ? opts.paths : browserifyPaths()
		};
		if ( opts.standalone ) {
			bopts.standalone = opts.standalone;
		} else if ( opts.exportName ) {
			bopts.exportName = opts.exportName;
		}
		if ( opts.external ) {
			bopts.external = opts.external;
		}
		debug( 'Browserify options: %s', JSON.stringify( bopts ) );
		return browserifyString( out, bopts, onBundle );
	}
	// TODO: support other bundlers

	/**
	* Callback invoked upon generating a browserify bundle.
	*
	* @private
	* @param {(Error|null)} error - error object
	* @param {(string|Buffer)} bundle - bundle
	* @returns {void}
	*/
	function onBundle( error, bundle ) {
		if ( error ) {
			debug( 'Encountered an error when generating a browserify bundle: %s', error.message );
			return done( error );
		}
		debug( 'Successfully generated a browserify bundle.' );
		done( null, bundle );
	}

	/**
	* Callback invoked upon completion.
	*
	* @private
	* @param {(Error|null)} error - error object
	* @param {(string|Buffer)} bundle - bundle
	* @returns {void}
	*/
	function done( error, bundle ) {
		if ( error ) {
			return cb( error );
		}
		cb( null, bundle );
	}
}


// EXPORTS //

module.exports = bundle;
