'use strict';

// MODULES //

var instanceOf = require( '@stdlib/assert/instance-of' );
var namespaces = require( '@stdlib/_tools/pkgs/namespaces' );


// MAIN //

/**
* Returns a list of stdlib namespaces.
*
* @private
* @returns {StringArray} list of namespaces
*
* @example
* var names = list();
*/
function list() {
	var names = namespaces.sync();
	if ( instanceOf( names, Error ) ) {
		throw names;
	}
	return names;
}


// EXPORTS //

module.exports = list();
