'use strict';

// MAIN //

/**
* Recommends a version bump based on a list of commits.
*
* @param {Array<Object>} commits - conventional changelog formatted commit message objects
* @returns {(string|null)} - `major`, `minor`, `patch`, or null
*/
function recommendVersionBump( commits ) {
	var hasFeature;
	var hasFix;
	var commit;
	var i;
	var j;

	for ( i = 0; i < commits.length; i++ ) {
		commit = commits[ i ];
		for ( j = 0; j < commit.notes.length; j++ ) {
			if ( commit.notes[ j ].title === 'BREAKING CHANGE' ) {
				return 'major';
			}
		}
		if ( commit.type === 'feat' ) {
			hasFeature = true;
		} else if ( commit.type === 'fix' ) {
			hasFix = true;
		}
	}
	if ( hasFeature ) {
		return 'minor';
	}
	if ( hasFix) {
		return 'patch';
	}
	return null;
}


// EXPORTS //

module.exports = recommendVersionBump;
