'use strict';

var valid = [];
var test;

test = {
	'code': [
		'function fizzBuzz() {',
		'  // This comment is okay...',
		'  var out;',
		'  var i;',
		'',
		'  for ( i = 1; i <= 100; i++ ) {',
		'    // This comment is okay...',
		'    out = ( i % 5 === 0 ) ? "Buzz" : ( i % 3 === 0 ) ? "Fizz" : i;',
		'    console.log( out );',
		'  }',
		'}'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'/**',
		'* Squares a number.',
		'*',
		'* @param {number} x - input value',
		'* @returns {number} x*x',
		'* @example',
		'* var y = square( 3.0 );',
		'* // returns 9.0',
		'*',
		'* // returns 4.0',
		'*/',
		'function square( x ) {',
		'  return x*x;',
		'}'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'var isLowercase = require( \'@stdlib/assert/is-lowercase\' );',
		'',
		'var bool = isLowercase( \'hello\' );',
		'',
		'bool = isLowercase( \'\' );',
		'',
		'bool = isLowercase( \'Hello\' );'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'#!/usr/bin/env node',
		'',
		'var isLowercase = require( \'@stdlib/assert/is-lowercase\' );',
		'',
		'console.log( isLowercase( \'hello\' ) );',
		'// => false',
		'',
		'console.log( isLowercase( \'\' ) );',
		'// => false',
		'',
		'console.log( isLowercase( \'Hello\' ) );',
		'// => true'
	].join( '\n' )
};
valid.push( test );


// EXPORTS //

module.exports = valid;
