'use strict';

var valid = [];
var test;

test = {
	'code': [
		'var isLowercase = require( \'@stdlib/assert/is-lowercase\' );',
		'',
		'console.log( isLowercase( \'hello\' ) );',
		'// => true',
		'',
		'console.log( isLowercase( \'\' ) );',
		'// => false',
		'',
		'console.log( isLowercase( \'Hello\' ) );',
		'// => false'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		' var obj = { \'beep\': \'boop\' };',
		'',
		'console.log( obj );',
		'// => { \'beep\': \'boop\' }',
		'',
		'delete obj.beep;',
		'',
		'console.log( obj );',
		'// => {}'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'var isLowercase = require( \'@stdlib/assert/is-lowercase\' );',
		'',
		'var bool = isLowercase( \'hello\' );',
		'// returns true',
		'',
		'bool = isLowercase( \'\' );',
		'// returns false',
		'',
		'bool = isLowercase( \'Hello\' );',
		'// returns false'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'var isLowercase = require( \'@stdlib/assert/is-lowercase\' );',
		'',
		'// Checks for lowercase:',
		'var bool = isLowercase( \'hello\' );',
		'// returns true',
		'',
		'bool = isLowercase( \'\' );',
		'// returns false',
		'',
		'bool = isLowercase( \'Hello\' );',
		'// returns false'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		' var SharedArrayBuffer = require( \'@stdlib/array/shared-buffer\' );',
		'',
		' try {',
		'     var bool = isSharedArrayBuffer( new SharedArrayBuffer( 10 ) );',
		'     // returns true',
		' } catch ( err ) {',
		'     console.log( \'Environment does not support SharedArrayBuffers.\' );',
		' }',
		''
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'var functionName = require( \'@stdlib/utils/function-name\' );',
		'',
		'console.log( functionName( Math.sqrt ) ); // eslint-disable-line',
		'// => \'sqrt\'',
		'',
		'console.log( functionName( Float64Array ) );',
		'// => \'Float64Array\'',
		'',
		'console.log( functionName( Date ) );',
		'// => \'Date\'',
		'',
		'console.log( functionName( String ) );',
		'// => \'String\''
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'var startsWith = require( \'@stdlib/string/starts-with\' );',
		'',
		'var str = \'Fair is foul, and foul is fair, hover through fog and filthy air\';',
		'',
		'console.log( startsWith( str, \'Fair\' ) );',
		'// => true',
		'',
		'console.log( startsWith( str, \'fair\' ) );',
		'// => false',
		'',
		'console.log( startsWith( str, \'foul\', 8 ) );',
		'// => true',
		'',
		'console.log( startsWith( str, \'filthy\', -10 ) );',
		'// => true',
		'',
		''
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'\'use strict\';',
		'',
		'var randu = require( \'@stdlib/random/base/randu\' );',
		'var Float64Array = require( \'@stdlib/array/float64\' );',
		'var sparkline = require( \'@stdlib/plot/sparklines/unicode\' );',
		'',
		'var chart;',
		'var data;',
		'var str;',
		'var i;',
		'',
		'// Generate some random data...',
		'data = new Float64Array( 50 );',
		'for ( i = 0; i < data.length; i++ ) {',
		'	data[ i ] = randu() * 100.0;',
		'}',
		'',
		'// Create a sparkline chart:',
		'chart = sparkline( data );',
		'',
		'// Render the chart as a column chart:',
		'chart.type = \'column\';',
		'str = chart.render();',
		'console.log( str );',
		'// => \'...\'',
		'',
		'// Render the chart as a line chart:',
		'chart.type = \'line\';',
		'str = chart.render();',
		'console.log( str );',
		'// => \'...\''
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'\'use strict\';',
		'',
		'var tokenize = require( \'@stdlib/nlp/tokenize\' );',
		'',
		'console.log( tokenize( \'Hello World!\' ) );',
		'// => [ \'Hello\', \'World\', \'!\' ]',
		'',
		'console.log( tokenize( \'\' ) );',
		'// => []',
		'',
		'var str = \'Lorem ipsum dolor sit amet, consetetur sadipscing elitr, sed diam nonumy eirmod.\';',
		'console.log( tokenize( str ) );',
		'/* =>',
		'	[',
		'		\'Lorem\',',
		'		\'ipsum\',',
		'		\'dolor\',',
		'		\'sit\',',
		'		\'amet\',',
		'		\',\',',
		'		\'consetetur\',',
		'		\'sadipscing\',',
		'		\'elitr\',',
		'		\',\',',
		'		\'sed\',',
		'		\'diam\',',
		'		\'nonumy\',',
		'		\'eirmod\',',
		'		\'.\'',
		'	]',
		'*/'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'var contains = require( \'@stdlib/assert/contains\' )',
		'',
		'var v = contains( false, \'abc\' );',
		'// throws <TypeError>'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'var iterator = require( \'@stdlib/random/iter/uniform\' );',
		'var it = iterator( 13.0, 9.0 );',
		'// throws <RangeError>'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'var iterator = require( \'@stdlib/random/iter/uniform\' );',
		'var it = iterator( 13.0, 9.0 );',
		'// throws <RangeError>',
		'',
		'var it = iterator( 3.0, 1.0 );',
		'// throws <RangeError>'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'var reviveError = require( \'@stdlib/error/reviver\' );',
		'',
		'var str = \'{"type":"TypeError","message":"beep"}\';',
		'',
		'var err = JSON.parse( str, reviveError );',
		'// returns <TypeError>'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'var normalize = require( \'@stdlib/number/float64/base/normalize\' );',
		'var PINF = require( \'@stdlib/constants/float64/pinf\' );',
		'var NINF = require( \'@stdlib/constants/float64/ninf\' );',
		'',
		'var out = normalize( PINF );',
		'// returns [ Infinity, 0 ]',
		'',
		'out = normalize( NINF );',
		'// returns [ -Infinity, 0 ]'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'var Number = require( \'@stdlib/number/ctor\' );',
		'',
		'var v = new Number( 5.0 );',
		'// returns <Number>'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'var mapKeys = require( \'@stdlib/utils/map-keys\' );',
		'',
		'function transform( key, value ) {',
		'  return key + \':\' + value;',
		'}',
		'',
		'var obj1 = {',
		'  \'a\': \'beep\',',
		'  \'b\': \'boop\',',
		'  \'c\': \'foo\',',
		'  \'d\': \'bar\'',
		'};',
		'',
		'var obj2 = mapKeys( obj1, transform );',
		'',
		'console.dir( obj2 );',
		'// => { \'a:beep\': \'beep\', \'b:boop\': \'boop\', \'c:foo\': \'foo\', \'d:bar\': \'bar\' }'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'var Float64Array = require( \'@stdlib/array/float64\' );',
		'var shift = require( \'@stdlib/utils/shift\' );',
		'',
		'var arr = new Float64Array( 2 );',
		'arr[ 0 ] = 1.0;',
		'arr[ 1 ] = 2.0;',
		'',
		'var out = shift( arr );',
		'// returns [ <Float64Array>[ 2.0 ], 1.0 ]'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'var pickBy = require( \'@stdlib/utils/pick-by\' );',
		'',
		'function predicate( key, value ) {',
		'  return ( typeof value === \'number\' );',
		'}',
		'',
		'var obj1 = {',
		'  \'a\': \'1\',',
		'  \'b\': 2,',
		'  \'c\': NaN,',
		'  \'d\': null',
		'};',
		'',
		'var obj2 = pickBy( obj1, predicate );',
		'// returns { \'b\': 2, \'c\': NaN }'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'var inheritedPropertyDescriptors = require( \'@stdlib/utils/inherited-property-descriptors\' );',
		'var inherit = require( \'@stdlib/utils/inherit\' );',
		'',
		'function Bar() {',
		'  return this;',
		'}',
		'',
		'Bar.prototype.boop = \'beep\';',
		'',
		'function Foo() {',
		'  Bar.call( this );',
		'  this.a = \'b\';',
		'  return this;',
		'}',
		'',
		'inherit( Foo, Bar );',
		'Foo.prototype.beep = \'boop\';',
		'',
		'var f = new Foo();',
		'var desc = inheritedPropertyDescriptors( f, 1 );',
		'// returns { \'beep\': {...}, ... }'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'var reFromString = require( \'@stdlib/utils/regexp-from-string\' );',
		'',
		'var re = reFromString( \'/beep/\' );',
		'// returns /beep/'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'var pluck = require( \'@stdlib/utils/pluck\' );',
		'',
		'var arr = [',
		'  { \'a\': 1, \'b\': 2 },',
		'  null,',
		'  void 0,',
		'  { \'a\': 0.5, \'b\': 3 }',
		'];',
		'',
		'var out = pluck( arr, \'a\' );',
		'// returns [ 1, , , 0.5 ]'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'var invertBy = require( \'@stdlib/utils/object-inverse-by\' );',
		'function transform( key, value, o ) {',
		'  if ( key === \'name\' ) {',
		'    return value;',
		'  }',
		'  return o.name + \':\' + value;',
		'}',
		'var obj = {',
		'  \'name\': \'foo\',',
		'  \'a\': \'beep\',',
		'  \'b\': \'boop\'',
		'};',
		'var out = invertBy( obj, transform );',
		'// returns { \'foo\': \'name\', \'foo:beep\': \'a\', \'foo:boop\': \'b\' }'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'var push = require( \'@stdlib/utils/push\' );',
		'var arr = [];',
		'',
		'push( arr, 3 );',
		'// arr => [ 3 ]'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'var annotation = \'ndarray( \\\'generic\\\', [ 3, 4, 5, 6, 7, 8 ], [ 3, 2 ], [ 2, 1 ], 0, \\\'row-major\\\' )\'',
		'console.log( annotation );',
		'// => "ndarray( \'generic\', [ 3, 4, 5, 6, 7, 8 ], [ 3, 2 ], [ 2, 1 ], 0, \'row-major\' )"'
	].join( '\n' )
};
valid.push( test );


// EXPORTS //

module.exports = valid;
