'use strict';

var Linter = require( 'eslint' ).Linter;
var rule = require( './../lib' );

var linter = new Linter();
var result;
var code;

code = [
	'function square( x ) {',
	'  var out;',
	'  // Square the number:',
	'  out = x*x;',
	'  return out;',
	'}'
].join( '\n' );

linter.defineRule( 'empty-line-before-comment', rule );

result = linter.verifyAndFix( code, {
	'rules': {
		'empty-line-before-comment': 'error'
	}
});
console.log( result );
