'use strict';

var invalid = [];
var test;

test = {
	'code': [
		'function square( x ) {',
		'  var x;',
		'  // Square the number:',
		'  return x*x;',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Missing empty line before comment',
			'type': null
		}
	],
	'output': [
		'function square( x ) {',
		'  var x;',
		'',
		'  // Square the number:',
		'  return x*x;',
		'}'
	].join( '\n' )
};
invalid.push( test );

test = {
	'code': [
		'function fizzBuzz() {',
		'  var out;',
		'  var i;',
		'',
		'  for ( i = 1; i <= 100; i++ ) {',
		'    out = ( i % 5 === 0 ) ? "Buzz" : ( i % 3 === 0 ) ? "Fizz" : i;',
		'    // Print to console: ',
		'    console.log( out );',
		'  }',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Missing empty line before comment',
			'type': null
		}
	],
	'output': [
		'function fizzBuzz() {',
		'  var out;',
		'  var i;',
		'',
		'  for ( i = 1; i <= 100; i++ ) {',
		'    out = ( i % 5 === 0 ) ? "Buzz" : ( i % 3 === 0 ) ? "Fizz" : i;',
		'',
		'    // Print to console: ',
		'    console.log( out );',
		'  }',
		'}'
	].join( '\n' )
};
invalid.push( test );

test = {
	'code': [
		'function square( x ) {',
		'  var x;',
		'',
		'  // Square the number:',
		'  // y = x^2 = x*2',
		'  return x*x;',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Missing empty line before comment',
			'type': null
		}
	],
	'output': [
		'function square( x ) {',
		'  var x;',
		'',
		'  // Square the number:',
		'',
		'  // y = x^2 = x*2',
		'  return x*x;',
		'}'
	].join( '\n' )
};
invalid.push( test );

test = {
	'code': [
		'function makePerson() {',
		'  var person = {',
		'    // Job title:',
		'    \'title\': \'engineer\',',
		'    // Last name:',
		'    \'name\': \'Susan\'',
		'  };',
		'  return person;',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Missing empty line before comment',
			'type': null
		}
	],
	'output': [
		'function makePerson() {',
		'  var person = {',
		'    // Job title:',
		'    \'title\': \'engineer\',',
		'',
		'    // Last name:',
		'    \'name\': \'Susan\'',
		'  };',
		'  return person;',
		'}'
	].join( '\n' )
};
invalid.push( test );

test = {
	'code': [
		'function makePerson() {',
		'  var person = {',
		'    \'title\': \'engineer\',',
		'    \'name\': \'Susan\'',
		'  };',
		'  // Return the object:',
		'  return person;',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Missing empty line before comment',
			'type': null
		}
	],
	'output': [
		'function makePerson() {',
		'  var person = {',
		'    \'title\': \'engineer\',',
		'    \'name\': \'Susan\'',
		'  };',
		'',
		'  // Return the object:',
		'  return person;',
		'}'
	].join( '\n' )
};
invalid.push( test );


// EXPORTS //

module.exports = invalid;
