'use strict';

var valid = [];
var test;

test = {
	'code': [
		'function fizzBuzz() {',
		'  // This is a single-line comment',
		'  var out;',
		'  var i;',
		'',
		'  for ( i = 1; i <= 100; i++ ) {',
		'    /*',
		'    * This is a multi-line comment...',
		'    */',
		'    out = ( i % 5 === 0 ) ? "Buzz" : ( i % 3 === 0 ) ? "Fizz" : i;',
		'    console.log( out );',
		'  }',
		'}'
	].join( '\n' ),
	'filename': 'fizz_buzz.js'
};
valid.push( test );

test = {
	'code': [
		'// MODULES //',
		'',
		'var setReadOnly = require( \'@stdlib/utils/define-read-only-property\' );',
		'',
		'',
		'// MAIN //',
		'',
		'/**',
		'* Top-level namespace.',
		'*',
		'* @namespace ns',
		'*/',
		'var ns = {};',
		'',
		'/**',
		'* @name error2json',
		'* @memberof ns',
		'* @readonly',
		'* @type {Function}',
		'* @see {@link module:@stdlib/error/to-json}',
		'*/',
		'setReadOnly( ns, \'error2json\', require( \'@stdlib/error/to-json\' ) );',
		'',
		'/**',
		'* @name reviveError',
		'* @memberof ns',
		'* @readonly',
		'* @type {Function}',
		'* @see {@link module:@stdlib/error/reviver}',
		'*/',
		'setReadOnly( ns, \'reviveError\', require( \'@stdlib/error/reviver\' ) );'
	].join( '\n' ),
	'filename': 'index.js'
};
valid.push( test );


// EXPORTS //

module.exports = valid;
