'use strict';

var Linter = require( 'eslint' ).Linter;
var rule = require( './../lib' );

var linter = new Linter();
var result;
var code;

// Generate our source code:
code = [
	'/**',
	'* Beep boop.',
	'*',
	'* > This a blockquote.',
	'*',
	'* ## Heading',
	'*',
	'* >   Another blockquote.',
	'*/',
	'function beep() {',
	'    console.log( "boop" );',
	'}'
].join( '\n' );

// Register the ESLint rule:
linter.defineRule( 'jsdoc-blockquote-indentation', rule );

// Lint the code:
result = linter.verify( code, {
	'rules': {
		'jsdoc-blockquote-indentation': [ 'error', 'consistent' ]
	}
});
console.log( result );
