'use strict';

// MODULES //

var parseJSDoc = require( 'doctrine' ).parse;
var remark = require( 'remark' );
var remarkLint = require( 'remark-lint' );
var remarkPlugin = require( 'remark-lint-fenced-code-marker' );
var repeat = require( '@stdlib/string/repeat' );
var isObject = require( '@stdlib/assert/is-object' );
var findJSDoc = require( '@stdlib/_tools/eslint/utils/find-jsdoc' );


// VARIABLES //

var RE_FENCE = /[`~]{3}/;
var DEFAULT = '`';
var DOPTS = {
	'sloppy': true,
	'unwrap': true
};
var rule;


// FUNCTIONS //

/**
* Rule for enforcing a specific Markdown fenced code marker in JSDoc descriptions.
*
* @param {Object} context - ESLint context
* @returns {Object} validators
*/
function main( context ) {
	var setting;
	var source;
	var config;
	var lint;

	setting = context.options[ 0 ] || DEFAULT;
	config = {
		'plugins': [
			remarkLint,
			[ remarkPlugin, [ 'error', setting ] ]
		]
	};
	lint = remark().use( config ).processSync; // eslint-disable-line node/no-sync
	source = context.getSourceCode();

	return {
		'FunctionExpression:exit': validate,
		'FunctionDeclaration:exit': validate,
		'VariableDeclaration:exit': validate,
		'ExpressionStatement:exit': validate
	};

	/**
	* Lints JSDoc descriptions.
	*
	* @private
	* @param {ASTNode} node - AST node
	*/
	function validate( node ) {
		var jsdoc;
		var vfile;
		var ast;

		jsdoc = findJSDoc( source, node );
		if ( isObject( jsdoc ) ) {
			ast = parseJSDoc( jsdoc.value, DOPTS );
			if ( ast.description ) {
				vfile = lint( ast.description );
				if ( vfile.messages.length ) {
					reportErrors( vfile.messages, jsdoc.loc );
				}
			}
		}
	}

	/**
	* Reports Markdown lint errors.
	*
	* @private
	* @param {ObjectArray} errors - Markdown lint errors
	* @param {Object} location - JSDoc location information
	*/
	function reportErrors( errors, location ) {
		var err;
		var msg;
		var loc;
		var i;

		for ( i = 0; i < errors.length; i++ ) {
			err = errors[ i ];
			msg = err.message;
			loc = locationInfo({
				'startLine': location.start.line + err.location.start.line,
				'startColumn': location.start.column + err.location.start.column + 1,  // Note: we assume that 1 space separates `*` from JSDoc description content (e.g., `* ## Beep`)
				'endLine': location.start.line + err.location.end.line,
				'endColumn': location.start.column + err.location.end.column + 1  // Note: we assume that 1 space separates `*` from JSDoc description content (e.g., `* ## Beep`)
			});
			report( msg, loc );
		}
	}

	/**
	* Creates a location object.
	*
	* @private
	* @param {Object} options - function options
	* @returns {Object} location info
	*/
	function locationInfo( options ) {
		return {
			'start': {
				'line': options.startLine,
				'column': options.startColumn
			},
			'end': {
				'line': options.endLine,
				'column': options.endColumn
			}
		};
	}

	/**
	* Reports an error message.
	*
	* @private
	* @param {string} msg - error message
	* @param {Object} loc - error location info
	*/
	function report( msg, loc ) {
		context.report({
			'node': null,
			'message': msg,
			'loc': loc,
			'fix': fix
		});

		/**
		* Fixes a lint error.
		*
		* @private
		* @param {Object} fixer - ESLint fixer
		* @returns {Object} fix
		*/
		function fix( fixer ) {
			var replacement;
			var fence;
			var range;
			var text;

			range = [
				source.getIndexFromLoc( loc.start ),
				source.getIndexFromLoc( loc.end )
			];
			text = source.text.slice( range[ 0 ], range[ 1 ] );
			if ( setting === 'consistent' ) {
				fence = RE_FENCE.exec( source.text )[ 0 ];
			} else {
				fence = repeat( setting, 3 );
			}
			replacement = fence + text.substring( 3, text.length-3 ) + fence;
			return fixer.replaceTextRange( range, replacement );
		}
	}
}


// MAIN //

rule = {
	'meta': {
		'docs': {
			'description': 'enforce a specific Markdown fenced code marker in JSDoc descriptions'
		},
		'schema': [
			{
				'type': 'string'
			}
		],
		'fixable': 'code'
	},
	'create': main
};


// EXPORTS //

module.exports = rule;
