'use strict';

// MODULES //

var parseExpressionAt = require( 'acorn' ).parseExpressionAt;
var parseJS = require( 'acorn' ).parse;
var parseJSDoc = require( 'doctrine' ).parse;
var isString = require( '@stdlib/assert/is-string' ).isPrimitive;
var isObject = require( '@stdlib/assert/is-object' );
var replace = require( '@stdlib/string/replace' );
var startsWith = require( '@stdlib/string/starts-with' );
var endsWith = require( '@stdlib/string/ends-with' );
var findJSDoc = require( '@stdlib/_tools/eslint/utils/find-jsdoc' );


// VARIABLES //

var RE_COMMA_DOTS = /,\s?\.\.\./g;
var RE_ANNOTATION = /^\s*(?:e\.g\.,){0,1} (returns|=>|throws) {0,1}([\s\S]*)/;
var RE_TYPE_IDENTIFIER = /<[A-Z0-9]+>/gi;
var RE_TRAILING_COMMENT = /; (\/\/|=>)[^\n]*\n/g;
var DOPTS = {
	'sloppy': true,
	'unwrap': true,
	'tags': [ 'example' ]
};
var rule;


// FUNCTIONS //

/**
* Checks whether a node for an object expression contains only quoted properties.
*
* @private
* @param {ASTNode} obj - object to examine
* @returns {boolean} boolean indicating whether the object has only keys wrapped in single quotes
*/
function hasQuotedKeys( obj ) {
	var props;
	var elem;
	var key;
	var val;
	var i;
	var j;

	props = obj.properties;
	for ( i = 0; i < props.length; i++ ) {
		key = props[ i ].key;
		val = props[ i ].value;
		if (
			!key.raw ||
			!startsWith( key.raw, '\'' ) ||
			!endsWith( key.raw, '\'' )
		) {
			return false;
		}
		if ( val && val.type === 'ObjectExpression' ) {
			if ( !hasQuotedKeys( val ) ) {
				return false;
			}
		}
		else if ( val && val.type === 'ArrayExpression' ) {
			for ( j = 0; j < val.elements.length; j++ ) {
				elem = val.elements[ j ];
				if ( elem && elem.type === 'ObjectExpression' ) {
					if ( !hasQuotedKeys( elem ) ) {
						return false;
					}
				}
			}
		}
	}
	return true;
}

/**
* Checks whether a comment is a return annotation and, if so, whether any property names in it are quoted using single quotes.
*
* @private
* @param {string} comment - comment to examine
* @returns {(string|null)} error message or null
*/
function checkComment( comment ) {
	var matches;
	var elem;
	var node;
	var ast;
	var i;

	matches = comment.value.match( RE_ANNOTATION );
	if ( matches ) {
		node = matches[ 2 ];
		if ( !isString( node ) ) {
			return null;
		}
		// Replace dots used to skip over array elements:
		node = replace( node, '...,', '' );
		node = replace( node, RE_COMMA_DOTS, '' );

		node = replace( node, '[...]', '[]' );
		node = replace( node, '{...}', '{}' );

		// Replace `~` characters used to indicate rounded values:
		node = replace( node, '~', '' );

		// Replace any type identifiers such as <Float64Array>:
		node = replace( node, RE_TYPE_IDENTIFIER, '' );

		if ( node.length === 0 ) {
			return null;
		}
		ast = parseExpressionAt( node );
		if ( ast && ast.type === 'ArrayExpression' ) {
			for ( i = 0; i < ast.elements.length; i++ ) {
				elem = ast.elements[ i ];
				if ( elem && elem.type === 'ObjectExpression' ) {
					if ( !hasQuotedKeys( elem ) ) {
						return 'Use single quotes around property names';
					}
				}
			}
		}
		if ( ast && ast.type === 'ObjectExpression' ) {
			if ( !hasQuotedKeys( ast ) ) {
				return 'Use single quotes around property names';
			}
		}
	}
	return null;
}

/**
* Rule for validating that property names in return annotations inside of example code are quoted using single quotes.
*
* @param {Object} context - ESLint context
* @returns {Object} validators
*/
function main( context ) {
	var source = context.getSourceCode();

	/**
	* Reports the error message.
	*
	* @private
	* @param {ASTNode} node - node to examine
	* @param {string} msg - error message
	*/
	function report( node, msg ) {
		context.report({
			'node': node,
			'message': msg
		});
	}

	/**
	* Checks whether return annotations in example code follow marker style conventions.
	*
	* @private
	* @param {ASTNode} node - node to examine
	*/
	function validate( node ) {
		var exampleAST;
		var comments;
		var jsdoc;
		var descr;
		var tags;
		var ast;
		var msg;
		var tag;
		var i;
		var j;

		jsdoc = findJSDoc( source, node );
		if ( isObject( jsdoc ) ) {
			ast = parseJSDoc( jsdoc.value, DOPTS );
			tags = ast.tags;
			for ( i = 0; i < tags.length; i++ ) {
				tag = tags[ i ];
				comments = [];
				descr = tag.description;

				// Handle special case where example code contains JSDoc comments with escaped ending:
				descr = replace( descr, '\\/', '/' );

				// Replace trailing comments to ensure that they are not mistakenly regarded as return annotations:
				descr = replace( descr, RE_TRAILING_COMMENT, ';\n' );

				try {
					exampleAST = parseJS( descr, {
						'onComment': comments,
						'locations': true
					});
					for ( j = 0; j < comments.length; j++ ) {
						msg = checkComment( comments[ j ], exampleAST );
						if ( msg ) {
							report( node, msg );
						}
					}
				} catch ( err ) {
					report( node, 'Error while parsing code: '+err.message );
				}
			}
		}
	}

	return {
		'FunctionExpression:exit': validate,
		'FunctionDeclaration:exit': validate,
		'VariableDeclaration:exit': validate,
		'ExpressionStatement:exit': validate
	};
}


// MAIN //

rule = {
	'meta': {
		'docs': {
			'description': 'enforce that property names in return annotations inside of example code are quoted using single quotes'
		},
		'schema': []
	},
	'create': main
};


// EXPORTS //

module.exports = rule;
