'use strict';

var invalid = [];
var test;

test = {
	'code': [
		'/**',
		'* Squares a number.',
		'*',
		'* @param {number} x - input value',
		'* @returns {number} x*x',
		'* @example',
		'* var y = square( 3.0 );',
		'* // returns 9.0',
		'*',
		'* console.log( square( 2.0 ) );',
		'* // returns 4.0',
		'*/',
		'function square( x ) {',
		'  return x*x;',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Only include `// returns` after variable declarations or assignment expressions (use `=>` after `console.log`)',
			'type': null
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'/**',
		'* Returns a pseudo-random number on [0,1].',
		'* ',
		'* @returns {number} uniform random number',
		'*',
		'* @example',
		'* var y = rand();',
		'* // e.g., => 0.5363925252089496',
		'*/',
		'function rand( x ) {',
		'  return Math.random();',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Use `// returns` after variable declarations or assignment expressions instead of `=>`',
			'type': null
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'/**',
		'* Squares a number.',
		'*',
		'* @param {number} x - input value',
		'* @returns {number} x*x',
		'* @example',
		'* var y = square( 3.0 );',
		'* // returns 9.0',
		'*',
		'* // returns 4.0',
		'*/',
		'function square( x ) {',
		'  return x*x;',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Encountered an orphaned return annotation without a preceding node',
			'type': null
		}
	]
};
invalid.push( test );


// EXPORTS //

module.exports = invalid;
