'use strict';

var invalid = [];
var test;

test = {
	'code': [
		'var gammaln = require( \'@stdlib/math/base/special/gammaln\' );',
		'',
		'module.exports = {',
		'	\'gammaln\': gammaln',
		'};'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Only variables and functions should be assigned to `module.exports`',
			'type': 'AssignmentExpression'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'module.exports = \'bar\';'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Only variables and functions should be assigned to `module.exports`',
			'type': 'AssignmentExpression'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'module.exports = foo();',
		'',
		'function foo() {',
		'    return 3.14;',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Only variables and functions should be assigned to `module.exports`',
			'type': 'AssignmentExpression'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'module.exports = require( \'./betainc.js\' );'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Only variables and functions should be assigned to `module.exports`',
			'type': 'AssignmentExpression'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'var factory = require( \'./factory.js\' );',
		'',
		'module.exports = factory();'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Only variables and functions should be assigned to `module.exports`',
			'type': 'AssignmentExpression'
		}
	]
};
invalid.push( test );


// EXPORTS //

module.exports = invalid;
