'use strict';

var invalid = [];
var test;

test = {
	'code': [
		'var gammaln = require( \'@stdlib/math/base/special/gammaln\' );',
		'',
		'module.exports = exports = {',
		'	\'gammaln\': gammaln',
		'};',
		'',
		'var beta = require( \'@stdlib/math/base/special/beta\' );',
		'',
		'exports.beta = beta;'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Export statements should appear at the end of the file',
			'type': 'ExpressionStatement'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'var betainc = require( \'./betainc.js\' );',
		'',
		'module.exports = betainc;',
		'',
		'var gamma = require( \'./gamma.js\' );',
		'',
		'var foo = \'bar\';'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Export statements should appear at the end of the file',
			'type': 'ExpressionStatement'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'module.exports = foo;',
		'',
		'function foo() {',
		'    return 3.14;',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Export statements should appear at the end of the file',
			'type': 'ExpressionStatement'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'var betainc = require( \'./betainc.js\' );',
		'',
		'module.exports = betainc;',
		'',
		'var gamma = require( \'./gamma.js\' );',
		'',
		'module.exports = gamma;'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Export statements should appear at the end of the file',
			'type': 'ExpressionStatement'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'const gamma = () => {};',
		'',
		'export { betainc, gamma };',
		'function betainc() {}'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Export statements should appear at the end of the file',
			'type': 'ExportNamedDeclaration'
		}
	],
	'parserOptions': {
		'ecmaVersion': 6,
		'sourceType': 'module'
	}
};
invalid.push( test );

test = {
	'code': [
		'export const beep = 0;',
		'',
		'const boop = 1;',
		'',
		'export default boop;'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Export statements should appear at the end of the file',
			'type': 'ExportNamedDeclaration'
		}
	],
	'parserOptions': {
		'ecmaVersion': 6,
		'sourceType': 'module'
	}
};
invalid.push( test );

test = {
	'code': [
		'var randn = require( \'./randn.js\' );',
		'',
		'module.exports = randn;',
		'',
		'var factory = require( \'./factory.js\' );',
		'',
		'exports.factory = factory;'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Export statements should appear at the end of the file',
			'type': 'ExpressionStatement'
		}
	]
};
invalid.push( test );


// EXPORTS //

module.exports = invalid;
