'use strict';

var invalid = [];
var test;

test = {
	'code': [
		'var err = Error();'
	].join( '\n' ),
	'errors': [
		{
			'message': '`Error` constructor is invoked without the `new` keyword',
			'type': 'CallExpression'
		}
	],
	'output': [
		'var err = new Error();'
	].join( '\n' )
};
invalid.push( test );

test = {
	'code': [
		'var types = {};',
		'types.Error = Error;',
		'var err = types.Error();'
	].join( '\n' ),
	'errors': [
		{
			'message': '`Error` constructor is invoked without the `new` keyword',
			'type': 'CallExpression'
		}
	],
	'output': [
		'var types = {};',
		'types.Error = Error;',
		'var err = new types.Error();'
	].join( '\n' )
};
invalid.push( test );

test = {
	'code': [
		'var types = {};',
		'types.Error = Error;',
		'var err = types[ \'Error\' ]();'
	].join( '\n' ),
	'errors': [
		{
			'message': '`Error` constructor is invoked without the `new` keyword',
			'type': 'CallExpression'
		}
	],
	'output': [
		'var types = {};',
		'types.Error = Error;',
		'var err = new types[ \'Error\' ]();'
	].join( '\n' )
};
invalid.push( test );

test = {
	'code': [
		'// MAIN //',
		'',
		'function sqrt( x ) {',
		'  if ( x < 0 ) {',
		'    throw TypeError( \'must provide a non-negative number\' );',
		'  }',
		'  return Math.sqrt( x );',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': '`TypeError` constructor is invoked without the `new` keyword',
			'type': 'CallExpression'
		}
	],
	'output': [
		'// MAIN //',
		'',
		'function sqrt( x ) {',
		'  if ( x < 0 ) {',
		'    throw new TypeError( \'must provide a non-negative number\' );',
		'  }',
		'  return Math.sqrt( x );',
		'}'
	].join( '\n' )
};
invalid.push( test );


// EXPORTS //

module.exports = invalid;
