'use strict';

var invalid = [];
var test;

test = {
	'code': [
		'var re = RegExp( \'ab+c\' );'
	].join( '\n' ),
	'errors': [
		{
			'message': '`RegExp` constructor is invoked without the `new` keyword',
			'type': 'CallExpression'
		}
	],
	'output': [
		'var re = new RegExp( \'ab+c\' );'
	].join( '\n' )
};
invalid.push( test );

test = {
	'code': [
		'var types = {};',
		'types.RegExp = RegExp;',
		'var re = types.RegExp( \'[0-9]\' );'
	].join( '\n' ),
	'errors': [
		{
			'message': '`RegExp` constructor is invoked without the `new` keyword',
			'type': 'CallExpression'
		}
	],
	'output': [
		'var types = {};',
		'types.RegExp = RegExp;',
		'var re = new types.RegExp( \'[0-9]\' );'
	].join( '\n' )
};
invalid.push( test );

test = {
	'code': [
		'var types = {};',
		'types.RegExp = RegExp;',
		'var re = types[ \'RegExp\' ]( \'[0-9]\' );'
	].join( '\n' ),
	'errors': [
		{
			'message': '`RegExp` constructor is invoked without the `new` keyword',
			'type': 'CallExpression'
		}
	],
	'output': [
		'var types = {};',
		'types.RegExp = RegExp;',
		'var re = new types[ \'RegExp\' ]( \'[0-9]\' );'
	].join( '\n' )
};
invalid.push( test );

test = {
	'code': [
		'var re = RegExp( \'ab+c\', \'i\' );'
	].join( '\n' ),
	'errors': [
		{
			'message': '`RegExp` constructor is invoked without the `new` keyword',
			'type': 'CallExpression'
		}
	],
	'output': [
		'var re = new RegExp( \'ab+c\', \'i\' );'
	].join( '\n' )
};
invalid.push( test );

test = {
	'code': [
		'// MAIN //',
		'',
		'function createGlobalRegexp( str ) {',
		'  var re = RegExp( str, \'g\' );',
		'  return re;',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': '`RegExp` constructor is invoked without the `new` keyword',
			'type': 'CallExpression'
		}
	],
	'output': [
		'// MAIN //',
		'',
		'function createGlobalRegexp( str ) {',
		'  var re = new RegExp( str, \'g\' );',
		'  return re;',
		'}'
	].join( '\n' )
};
invalid.push( test );


// EXPORTS //

module.exports = invalid;
