'use strict';

var Linter = require( 'eslint' ).Linter;
var rule = require( './../lib' );

var linter = new Linter();
var result;
var code;

code = 'var y = Math.sqrt( 9.0 );';

linter.defineRule( 'no-builtin-math', rule );

result = linter.verify( code, {
	'rules': {
		'no-builtin-math': 'error'
	}
});
console.log( result );
