'use strict';

var invalid = [];
var test;

test = {
	'code': [
		'// VARIABLES //',
		'',
		'var E = Math.exp( 1.0 );'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Use the package `@stdlib/math/base/special/exp` instead of `Math.exp`',
			'type': 'MemberExpression'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'// VARIABLES //',
		'',
		'var PI_SQUARED = Math.PI*Math.PI;'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Use the package `@stdlib/constants/float64/pi` instead of `Math.PI`',
			'type': 'MemberExpression'
		},
		{
			'message': 'Use the package `@stdlib/constants/float64/pi` instead of `Math.PI`',
			'type': 'MemberExpression'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'// VARIABLES //',
		'',
		'var PI_CUBED = Math.pow( Math.PI, 3.0 );'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Use the package `@stdlib/math/base/special/pow` instead of `Math.pow`',
			'type': 'MemberExpression'
		},
		{
			'message': 'Use the package `@stdlib/constants/float64/pi` instead of `Math.PI`',
			'type': 'MemberExpression'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'// VARIABLES //',
		'',
		'function sqrt( x ) {',
		'  return Math.sqrt( x );',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Use the package `@stdlib/math/base/special/sqrt` instead of `Math.sqrt`',
			'type': 'MemberExpression'
		}
	]
};
invalid.push( test );


// EXPORTS //

module.exports = invalid;
