'use strict';

var Linter = require( 'eslint' ).Linter;
var rule = require( './../lib' );

var linter = new Linter();
var result;
var code;

code = [
	'function pow2( x ) {',
	'  var out;',
	'',
	'  /* */',
	'  out = x*x; //',
	'  return out;',
	'}'
].join( '\n' );

linter.defineRule( 'no-empty-comments', rule );

result = linter.verify( code, {
	'rules': {
		'no-empty-comments': 'error'
	}
});
console.log( result );
