'use strict';

var invalid = [];
var test;

test = {
	'code': [
		'function pow2( x ) {',
		'  //',
		'  return x*x;',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Empty comments are not allowed',
			'type': null
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'function fizzBuzz() {',
		'  var out;',
		'  var i;',
		'',
		'  for ( i = 1; i <= 100; i++ ) {',
		'    out = ( i % 5 === 0 ) ? "Buzz" : ( i % 3 === 0 ) ? "Fizz" : i;',
		'    /*               */',
		'    console.log( out );',
		'  }',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Empty comments are not allowed',
			'type': null
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'function makePerson() {',
		'  var person = {',
		'    /* */',
		'    \'title\': \'engineer\',',
		'',
		'    /* */',
		'    \'name\': \'Susan\'',
		'  };',
		'  return person;',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Empty comments are not allowed',
			'type': null
		},
		{
			'message': 'Empty comments are not allowed',
			'type': null
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'function square( x ) {',
		'  var out;',
		'  var x;',
		'',
		'  out = x*x;',
		'  //',
		'',
		'  return out;',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Empty comments are not allowed',
			'type': null
		}
	]
};
invalid.push( test );


// EXPORTS //

module.exports = invalid;
