'use strict';

// MODULES //

var RE_INDEX = /index\.(?:js|node|json)$/;


// VARIABLES //

var rule;


// FUNCTIONS //

/**
* Rule for validating that `require` calls do not explicitly require `index.<ext>` files.
*
* @param {Object} context - ESLint context
* @returns {Object} validators
*/
function main( context ) {
	/**
	* Reports the error message.
	*
	* @private
	* @param {ASTNode} node - node to report
	*/
	function report( node ) {
		context.report({
			'node': node,
			'message': 'do not explicitly require `index.<ext>` files'
		});
	}

	/**
	* Validates a `require` path.
	*
	* @private
	* @param {ASTNode} node - node to examine
	*/
	function validate( node ) {
		var requirePath;
		if ( node.callee.name === 'require' ) {
			requirePath = node.arguments[ 0 ].value;
			if ( RE_INDEX.test( requirePath ) ) {
				report( node );
			}
		}
	}

	return {
		'CallExpression': validate
	};
}


// MAIN //

rule = {
	'meta': {
		'docs': {
			'description': 'disallow `require()` statements which explicitly require `index.<ext>` files'
		},
		'schema': []
	},
	'create': main
};


// EXPORTS //

module.exports = rule;
