'use strict';

var invalid = [];
var test;

test = {
	'code': [
		'// MODULES //',
		'',
		'var betainc = require( \'@stdlib/math/base/special/betainc\' );',
		'var fs = require( \'fs\' );'
	].join( '\n' ),
	'errors': [
		{
			'message': '`require( \'fs\' )` should come before `require( \'@stdlib/math/base/special/betainc\' )`'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'// MODULES //',
		'',
		'var debug = require( \'debug\' );',
		'var fs = require( \'fs\' );'
	].join( '\n' ),
	'errors': [
		{
			'message': '`require( \'fs\' )` should come before `require( \'debug\' )`'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'// MODULES //',
		'',
		'var validate = require( \'./validate.js\' );',
		'var debug = require( \'debug\' );'
	].join( '\n' ),
	'errors': [
		{
			'message': '`require( \'debug\' )` should come before `require( \'./validate.js\' )`'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'// MODULES //',
		'',
		'var validate = require( \'./validate.js\' );',
		'var debug = require( \'debug\' );'
	].join( '\n' ),
	'options': [{
		'order': [ 'builtin', 'external', 'path' ]
	}],
	'errors': [
		{
			'message': '`require( \'debug\' )` should come before `require( \'./validate.js\' )`'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'var debug = require( \'debug\' )( \'links:create:sync\' );',
		'var resolve = require( \'path\' ).resolve;',
		'var instanceOf = require( \'@stdlib/assert/instance-of\' );',
		'var readJSON = require( \'@stdlib/fs/read-json\' ).sync;',
		'var writeFile = require( \'@stdlib/fs/write-file\' ).sync;',
		'var cwd = require( \'@stdlib/process/cwd\' );',
		'var config = require( \'./defaults.js\' );',
		'var validate = require( \'./validate.js\' );',
		'var insert = require( \'./insert.js\' );'
	].join( '\n' ),
	'errors': [
		{
			'message': '`require( \'path\' )` should come before `require( \'debug\' )`'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'// MODULES //',
		'',
		'var math = require( \'@stdlib/math\' );',
		'var debug = require( \'debug\' );',
		'var fs = require( \'fs\' );'
	].join( '\n' ),
	'options': [{
		'order': [ 'builtin', 'external', '/^@stdlib/', 'path' ]
	}],
	'errors': [
		{
			'message': '`require( \'debug\' )` should come before `require( \'@stdlib/math\' )`'
		},
		{
			'message': '`require( \'fs\' )` should come before `require( \'@stdlib/math\' )`'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'// MODULES //',
		'',
		'var abs = require( \'@stdlib/math/base/special/abs\' );',
		'var tape = require( \'tape\' );',
		'var main = require( \'./../lib/index.js\' );'
	].join( '\n' ),
	'options': [{
		'order': [ '/^tape$/', 'builtin', 'external', '/^@stdlib/', 'path' ]
	}],
	'errors': [
		{
			'message': '`require( \'tape\' )` should come before `require( \'@stdlib/math/base/special/abs\' )`'
		}
	]
};
invalid.push( test );


// EXPORTS //

module.exports = invalid;
