'use strict';

var valid = [];
var test;

test = {
	'code': [
		'// MODULES //',
		'',
		'var fs = require( \'fs\' );',
		'var betainc = require( \'@stdlib/math/base/special/betainc\' );',
		'var DEFAULTS = require( \'./defaults.json\' )'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'// MODULES //',
		'',
		'var math = require( \'@stdlib/math\' );'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'// MODULES //',
		'',
		'var fs = require( \'fs\' );',
		'var debug = require( \'debug\' );',
		'var validate = require( \'./validate.js\' )'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'// MODULES //',
		'',
		'var fs = require( \'fs\' );',
		'var debug = require( \'debug\' );',
		'var validate = require( \'./validate.js\' )'
	].join( '\n' ),
	'options': [{
		'order': [ 'builtin', 'external', 'path' ]
	}]
};
valid.push( test );

test = {
	'code': [
		'// MODULES //',
		'',
		'var validate = require( \'./validate.js\' )',
		'var fs = require( \'fs\' );',
		'var debug = require( \'debug\' );'
	].join( '\n' ),
	'options': [{
		'order': [ 'path', 'builtin', 'external' ]
	}]
};
valid.push( test );

test = {
	'code': [
		'// MODULES //',
		'',
		'var resolve = require( \'path\' ).resolve;',
		'var getKeys = require( \'object-keys\' ).shim();',
		'var isString = require( \'@stdlib/assert/is-string\' ).isPrimitive;',
		'var isFunction = require( \'@stdlib/assert/is-function\' );',
		'var readJSON = require( \'@stdlib/fs/read-json\' );',
		'var cwd = require( \'@stdlib/process/cwd\' );',
		'var config = require( \'./defaults.js\' );',
		'var validate = require( \'./validate.js\' );'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'// MODULES //',
		'',
		'var resolve = require( \'path\' ).resolve;',
		'var getKeys = require( \'object-keys\' ).shim();',
		'var isString = require( \'@stdlib/assert/is-string\' ).isPrimitive;',
		'var isFunction = require( \'@stdlib/assert/is-function\' );',
		'var readJSON = require( \'@stdlib/fs/read-json\' );',
		'var cwd = require( \'@stdlib/process/cwd\' );',
		'var config = require( \'./defaults.js\' );',
		'var validate = require( \'./validate.js\' );'
	].join( '\n' ),
	'options': [{
		'order': [ 'builtin', 'external', '/^@stdlib/', 'path' ]
	}]
};
valid.push( test );

test = {
	'code': [
		'// MODULES //',
		'',
		'var tape = require( \'tape\' );',
		'var objectKeys = require( \'object-keys\' );',
		'var TapeRecorder = require( \'tape-recorder\' );'
	].join( '\n' ),
	'options': [{
		'order': [ '/^tape$/', 'builtin', 'external', '/^@stdlib/', 'path' ]
	}]
};
valid.push( test );


// EXPORTS //

module.exports = valid;
