'use strict';

var invalid = [];
var test;

test = {
	'code': [
		' var value = [',
		'     {',
		'         \'a\': 1,',
		'         \'b\': true,',
		'         \'c\': [ 1, 2, 3 ]',
		'     }',
		' ];',
		' var out = copy( value );',
		' // returns [ { a: 1, b: true, c: [ 1, 2, 3 ] } ]',
		'',
		' var bool = ( value[0].c === out[0].c );',
		' // returns false'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Don\'t use unquoted property names',
			'type': null
		}
	]
};
invalid.push( test );

test = {
	'code': [
		' var value = [',
		'     {',
		'         \'a\': 1,',
		'         \'b\': true,',
		'         \'c\': [ 1, 2, 3 ]',
		'     }',
		' ];',
		' var out = copy( value );',
		' /* returns',
		'    [',
		'         {',
		'             \'a\': 1,',
		'             b: true,',
		'             c: [ 1, 2, 3 ]',
		'         }',
		'    ]',
		'*/',
		'',
		' var bool = ( value[0].c === out[0].c );',
		' // returns false'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Don\'t use unquoted property names',
			'type': null
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'var target = {',
		'  \'a\': \'beep\'',
		'};',
		'var source = {',
		'  \'b\': {',
		'    \'beep\': \'boop\'',
		'  }',
		'};',
		'',
		'var out = merge( target, source );',
		'// returns { \'a\': \'beep\', \'b\': { beep: \'boop\' } }'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Don\'t use unquoted property names',
			'type': null
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'var target = {',
		'  \'a\': \'beep\'',
		'};',
		'var source = {',
		'  \'b\': {',
		'    \'beep\': \'boop\'',
		'  }',
		'};',
		'',
		'var out = merge( target, source );',
		'// returns { \'a\': \'beep\', \'b\': [ { beep: \'boop\' } ] }'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Don\'t use unquoted property names',
			'type': null
		}
	]
};
invalid.push( test );


// EXPORTS //

module.exports = invalid;
