'use strict';

var Linter = require( 'eslint' ).Linter;
var rule = require( './../lib' );

var linter = new Linter();
var result;
var code;

var opts = {
	'rules': {
		'section-headers': 'error'
	}
};
linter.defineRule( 'section-headers', rule );

code = '// EXPORTS ';
result = linter.verify( code, opts );
console.log( result );
