'use strict';

var invalid = [];
var test;

test = {
	'code': [
		'function fizzBuzz() {',
		'  var i;',
		'  var out;',
		'',
		'  for ( i = 1; i <= 100; i++ ) {',
		'    out = ( i % 5 === 0 ) ? "Buzz" : ( i % 3 === 0 ) ? "Fizz" : i;',
		'    console.log( out );',
		'  }',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Variable declarations inside of function are not ordered by length (in decreasing order)',
			'type': null
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'function addFour( y ) {',
		'  var x = 4.0;',
		'  var out = x + y;',
		'  return out;',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Variable declarations inside of function are not ordered by length (in decreasing order)',
			'type': null
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'/**',
		'* Returns a pseudorandom number drawn from a triangular distribution with minimum support `a`, maximum support `b` and mode `c`.',
		'*',
		'* @private',
		'* @param {Function} rand - PRNG for generating uniformly distributed numbers',
		'* @param {number} a - minimum support',
		'* @param {number} b - maximum support',
		'* @param {number} c - mode',
		'* @returns {number} pseudorandom number',
		'*/',
		'function triangular( rand, a, b, c ) {',
		'  var x;',
		'  var fc;',
		'  var u;',
		'  fc = (c - a) / (b - a);',
		'  u = rand();',
		'  if ( u < fc ) {',
		'    x = (b - a) * (c - a);',
		'    return a + sqrt( x * u );',
		'  }',
		'  x = (b - a) * (b - c);',
		'  return b - sqrt( x * (1.0 - u) );',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Variable declarations inside of function are not ordered by length (in decreasing order)',
			'type': null
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'function addFour( y ) {',
		'  var x = 4.0;',
		'  var out = x + y;',
		'  return out;',
		'}'
	].join( '\n' ),
	'options': [{
		'order': 'decreasing'
	}],
	'errors': [
		{
			'message': 'Variable declarations inside of function are not ordered by length (in decreasing order)',
			'type': null
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'/**',
		'* Returns a pseudorandom number drawn from a triangular distribution with minimum support `a`, maximum support `b` and mode `c`.',
		'*',
		'* @private',
		'* @param {Function} rand - PRNG for generating uniformly distributed numbers',
		'* @param {number} a - minimum support',
		'* @param {number} b - maximum support',
		'* @param {number} c - mode',
		'* @returns {number} pseudorandom number',
		'*/',
		'function triangular( rand, a, b, c ) {',
		'  var fc;',
		'  var x;',
		'  var u;',
		'  fc = (c - a) / (b - a);',
		'  u = rand();',
		'  if ( u < fc ) {',
		'    x = (b - a) * (c - a);',
		'    return a + sqrt( x * u );',
		'  }',
		'  x = (b - a) * (b - c);',
		'  return b - sqrt( x * (1.0 - u) );',
		'}'
	].join( '\n' ),
	'options': [{
		'order': 'increasing'
	}],
	'errors': [
		{
			'message': 'Variable declarations inside of function are not ordered by length (in increasing order)',
			'type': null
		}
	]
};
invalid.push( test );


// EXPORTS //

module.exports = invalid;
