'use strict';

var createIssue = require( './../lib' );

var opts = {
	'token': '<your_token_goes_here>',
	'useragent': 'my-unique-agent',
	'body': 'Beep boop.'
};

createIssue( 'kgryte/test-repo1', 'Big bug.', opts, clbk );

/**
* Callback invoked upon receiving a response.
*
* @private
* @param {(Error|null)} error - error or null
* @param {Object} data - response data
* @param {Object} info - rate limit info
*/
function clbk( error, data, info ) {
	if ( info ) {
		console.error( info );
	}
	if ( error ) {
		if ( error instanceof Error ) {
			throw error;
		}
		console.error( error.message );
	} else {
		console.log( data );
	}
}
