'use strict';

/**
* Create an issue on a GitHub repository.
*
* @module @stdlib/_tools/github/create-issue
*
* @example
* var createIssue = require( '@stdlib/_tools/github/create-issue' );
*
* var opts = {
*     'token': '<your_token_goes_here>'
* };
* createIssue( 'kgryte/test-repo1', 'Big bug.', opts, onResponse );
*
* function onResponse( error, data, info ) {
*     // Check for rate limit info...
*     if ( info ) {
*         console.error( 'Limit: %d', info.limit );
*         console.error( 'Remaining: %d', info.remaining );
*         console.error( 'Reset: %s', (new Date( info.reset*1000 )).toISOString() );
*     }
*     if ( error ) {
*         if ( error instanceof Error ) {
*             throw error;
*         }
*         console.error( error.message );
*     } else {
*         console.log( JSON.stringify( data ) );
*     }
* }
*/

// MODULES //

var createIssue = require( './main.js' );


// EXPORTS //

module.exports = createIssue;
