'use strict';

// MODULES //

var isFunction = require( '@stdlib/assert/is-function' );
var isString = require( '@stdlib/assert/is-string' ).isPrimitive;
var format = require( '@stdlib/string/format' );
var copy = require( '@stdlib/utils/copy' );
var validate = require( './validate.js' );
var defaults = require( './defaults.json' );
var query = require( './query.js' );


// VARIABLES //

var DEFAULT_HTTP_PORT = 80;
var DEFAULT_HTTPS_PORT = 443;


// MAIN //

/**
* Creates an issue on a GitHub repository.
*
* @param {string} slug - repository slug
* @param {string} title - issue title
* @param {Options} options - function options
* @param {string} options.token - GitHub access token
* @param {string} [opts.protocol='https'] - request protocol
* @param {string} [opts.hostname='api.github.com'] - endpoint hostname
* @param {NonNegativeInteger} [opts.port] - endpoint port
* @param {string} [options.useragent] - user agent string
* @param {string} [options.body=""] - issue content
* @param {StringArray} [options.assignees] - usernames of assigned users
* @param {PositiveInteger} [options.milestone] - associated milestone number
* @param {StringArray} [options.labels] - issue labels
* @param {Callback} clbk - callback to invoke upon query completion
* @throws {TypeError} first argument must be a string
* @throws {TypeError} second argument must be a string
* @throws {TypeError} options argument must be an object
* @throws {TypeError} must provide valid options
* @throws {TypeError} last argument must be a function
*
* @example
* var opts = {
*     'token': '<your_token_goes_here>'
* };
* createIssue( 'kgryte/test-repo1', 'Big bug.', opts, onResponse );
*
* function onResponse( error, data, info ) {
*     // Check for rate limit info...
*     if ( info ) {
*         console.error( 'Limit: %d', info.limit );
*         console.error( 'Remaining: %d', info.remaining );
*         console.error( 'Reset: %s', (new Date( info.reset*1000 )).toISOString() );
*     }
*     if ( error ) {
*         if ( error instanceof Error ) {
*             throw error;
*         }
*         console.error( error.message );
*     } else {
*         console.log( JSON.stringify( data ) );
*     }
* }
*/
function createIssue( slug, title, options, clbk ) {
	var opts;
	var err;
	if ( !isString( slug ) ) {
		throw new TypeError( format( 'invalid argument. Repository slug must be a string. Value: `%s`.', slug ) );
	}
	if ( !isString( title ) ) {
		throw new TypeError( format( 'invalid argument. Issue title must be a string. Value: `%s`.', title ) );
	}
	opts = copy( defaults );
	err = validate( opts, options );
	if ( err ) {
		throw err;
	}
	if ( opts.port === null ) {
		if ( opts.protocol === 'https' ) {
			opts.port = DEFAULT_HTTPS_PORT;
		} else {
			opts.port = DEFAULT_HTTP_PORT;
		}
	}
	if ( !isFunction( clbk ) ) {
		throw new TypeError( format( 'invalid argument. Callback argument must be a function. Value: `%s`.', clbk ) );
	}
	query( slug, title, opts, done );

	/**
	* Callback invoked after receiving an API response.
	*
	* @private
	* @param {(Error|null)} error - error object
	* @param {Object} data - query data
	* @param {Object} info - response info
	*/
	function done( error, data, info ) {
		error = error || null;
		data = data || null;
		info = info || null;
		clbk( error, data, info );
	}
}


// EXPORTS //

module.exports = createIssue;
