'use strict';

// MODULES //

var isNonNegativeInteger = require( '@stdlib/assert/is-nonnegative-integer' );
var hasOwnProp = require( '@stdlib/assert/has-own-property' );
var isBoolean = require( '@stdlib/assert/is-boolean' ).isPrimitive;
var isObject = require( '@stdlib/assert/is-object' );
var isString = require( '@stdlib/assert/is-string' ).isPrimitive;
var isURI = require( '@stdlib/assert/is-uri' );
var format = require( '@stdlib/string/format' );


// MAIN //

/**
* Validates function options.
*
* @private
* @param {Object} opts - destination object
* @param {Object} options - options to validate
* @param {string} options.token - GitHub access token
* @param {string} [options.useragent] - user agent string
* @param {string} [options.org] - organization name
* @param {string} [options.desc] - repository description
* @param {string} [options.homepage] - repository homepage
* @param {number} [options.team] - team id (organizations)
* @param {string} [options.gitignore] - .gitignore template
* @param {string} [options.license] - LICENSE template
* @param {boolean} [options.private] - boolean indicating whether a repository should be private
* @param {boolean} [options.issues] - boolean indicating whether issues should be enabled
* @param {boolean} [options.projects] - boolean indicating whether project boards should be enabled
* @param {boolean} [options.wiki] - boolean indicating whether a repository should have a wiki
* @param {boolean} [options.downloads] - boolean indicating whether downloads should be enabled
* @param {boolean} [options.init] - boolean indicating whether to initialize the repository with an empty README
* @param {boolean} [options.allowSquashMerge] - boolean indicating whether to allow squash-merging
* @param {boolean} [options.allowMergeCommit] - boolean indicating whether to allow merging pull requests with a merge commit
* @param {boolean} [options.allowRebaseMerge] - boolean indicating whether to allow rebase-merging pull requests
* @returns {(Error|null)} error or null
*/
function validate( opts, options ) {
	if ( !isObject( options ) ) {
		return new TypeError( format( 'invalid argument. Options argument must be an object. Value: `%s`.', options ) );
	}
	opts.token = options.token;
	if ( !isString( opts.token ) ) {
		return new TypeError( format( 'invalid option. `%s` option must be a string. Option: `%s`.', 'token', opts.token ) );
	}
	if ( hasOwnProp( options, 'useragent' ) ) {
		opts.useragent = options.useragent;
		if ( !isString( opts.useragent ) ) {
			return new TypeError( format( 'invalid option. `%s` option must be a string. Option: `%s`.', 'useragent', opts.useragent ) );
		}
	}
	if ( hasOwnProp( options, 'org' ) ) {
		opts.org = options.org;
		if ( !isString( opts.org ) ) {
			return new TypeError( format( 'invalid option. `%s` option must be a string. Option: `%s`.', 'org', opts.org ) );
		}
	}
	if ( hasOwnProp( options, 'desc' ) ) {
		opts.desc = options.desc;
		if ( !isString( opts.desc ) ) {
			return new TypeError( format( 'invalid option. `%s` option must be a string. Option: `%s`.', 'desc', opts.desc ) );
		}
	}
	if ( hasOwnProp( options, 'homepage' ) ) {
		opts.homepage = options.homepage;
		if ( !isURI( opts.homepage ) ) {
			return new TypeError( format( 'invalid option. `%s` option must be a valid URI. Option: `%s`.', 'homepage', opts.homepage ) );
		}
	}
	if ( hasOwnProp( options, 'team' ) ) {
		opts.team = options.team;
		if ( !isNonNegativeInteger( opts.team ) ) {
			return new TypeError( format( 'invalid option. `%s` option must be a nonnegative integer. Option: `%s`.', 'team', opts.team ) );
		}
	}
	if ( hasOwnProp( options, 'gitignore' ) ) {
		opts.gitignore = options.gitignore;
		if ( !isString( opts.gitignore ) ) {
			return new TypeError( format( 'invalid option. `%s` option must be a string. Option: `%s`.', 'gitignore', opts.gitignore ) );
		}
	}
	if ( hasOwnProp( options, 'license' ) ) {
		opts.license = options.license;
		if ( !isString( opts.license ) ) {
			return new TypeError( format( 'invalid option. `%s` option must be a string. Option: `%s`.', 'license', opts.license ) );
		}
	}
	if ( hasOwnProp( options, 'private' ) ) {
		opts.private = options.private;
		if ( !isBoolean( opts.private ) ) {
			return new TypeError( format( 'invalid option. `%s` option must be a boolean. Option: `%s`.', 'private', opts.private ) );
		}
	}
	if ( hasOwnProp( options, 'issues' ) ) {
		opts.issues = options.issues;
		if ( !isBoolean( opts.issues ) ) {
			return new TypeError( format( 'invalid option. `%s` option must be a boolean. Option: `%s`.', 'issues', opts.issues ) );
		}
	}
	if ( hasOwnProp( options, 'projects' ) ) {
		opts.projects = options.projects;
		if ( !isBoolean( opts.projects ) ) {
			return new TypeError( format( 'invalid option. `%s` option must be a boolean. Option: `%s`.', 'projects', opts.projects ) );
		}
	}
	if ( hasOwnProp( options, 'wiki' ) ) {
		opts.wiki = options.wiki;
		if ( !isBoolean( opts.wiki ) ) {
			return new TypeError( format( 'invalid option. `%s` option must be a boolean. Option: `%s`.', 'wiki', opts.wiki ) );
		}
	}
	if ( hasOwnProp( options, 'downloads' ) ) {
		opts.downloads = options.downloads;
		if ( !isBoolean( opts.downloads ) ) {
			return new TypeError( format( 'invalid option. `%s` option must be a boolean. Option: `%s`.', 'downloads', opts.downloads ) );
		}
	}
	if ( hasOwnProp( options, 'init' ) ) {
		opts.init = options.init;
		if ( !isBoolean( opts.init ) ) {
			return new TypeError( format( 'invalid option. `%s` option must be a boolean. Option: `%s`.', 'init', opts.init ) );
		}
	}
	if ( hasOwnProp( options, 'allowSquashMerge' ) ) {
		opts.allowSquashMerge = options.allowSquashMerge;
		if ( !isBoolean( opts.allowSquashMerge ) ) {
			return new TypeError( format( 'invalid option. `%s` option must be a boolean. Option: `%s`.', 'allowSquashMerge', opts.allowSquashMerge ) );
		}
	}
	if ( hasOwnProp( options, 'allowMergeCommit' ) ) {
		opts.allowMergeCommit = options.allowMergeCommit;
		if ( !isBoolean( opts.allowMergeCommit ) ) {
			return new TypeError( format( 'invalid option. `%s` option must be a boolean. Option: `%s`.', 'allowMergeCommit', opts.allowMergeCommit ) );
		}
	}
	if ( hasOwnProp( options, 'allowRebaseMerge' ) ) {
		opts.allowRebaseMerge = options.allowRebaseMerge;
		if ( !isBoolean( opts.allowRebaseMerge ) ) {
			return new TypeError( format( 'invalid option. `%s` option must be a boolean. Option: `%s`.', 'allowRebaseMerge', opts.allowRebaseMerge ) );
		}
	}
	return null;
}


// EXPORTS //

module.exports = validate;
