tape( 'the function returns an error to a provided callback if an error is encountered when creating a repository', function test( t ) {
	var create;
	var opts;

	create = proxyquire( './../lib/main.js', {
		'./factory.js': factory
	});

	opts = getOpts();
	create( 'powm1', opts, done );

	function factory( opts, clbk ) {
		return function create() {
			setTimeout( onTimeout, 0 );
			function onTimeout() {
				clbk( new Error( 'beep' ), null, info );
			}
		};
	}

	function done( error ) {
		t.ok( error instanceof Error, 'error instance' );
		t.equal( error.message, 'beep' );
		t.end();
	}
});

tape( 'the function returns response data to a provided callback', function test( t ) {
	var expected;
	var create;
	var opts;

	create = proxyquire( './../lib/main.js', {
		'./factory.js': factory
	});

	expected = data;

	opts = getOpts();
	create( 'erfc', opts, done );

	function factory( opts, clbk ) {
		return function create() {
			setTimeout( onTimeout, 0 );
			function onTimeout() {
				clbk( null, data, info );
			}
		};
	}

	function done( error, data ) {
		if ( error ) {
			t.fail( error.message );
		}
		t.deepEqual( data, expected, 'deep equal' );
		t.end();
	}
});

tape( 'the function returns rate limit info to a provided callback', function test( t ) {
	var expected;
	var create;
	var opts;

	create = proxyquire( './../lib/main.js', {
		'./factory.js': factory
	});

	expected = info;

	opts = getOpts();
	create( 'log1p', opts, done );

	function factory( opts, clbk ) {
		return function create() {
			setTimeout( onTimeout, 0 );
			function onTimeout() {
				clbk( null, data, info );
			}
		};
	}

	function done( error, data, info ) {
		if ( error ) {
			t.fail( error.message );
		}
		t.deepEqual( info, expected, 'deep equal' );
		t.end();
	}
});
