'use strict';

/**
* Create a GitHub OAuth access token.
*
* @module @stdlib/_tools/github/create-token
*
* @example
* var createToken = require( '@stdlib/_tools/github/create-token' );
*
* var opts = {
*     'username': 'beep',
*     'password': 'boop',
*     'scopes': [ 'public_repo', 'read:org' ],
*     'note': 'beep'
* };
*
* createToken( opts, clbk );
*
* function clbk( error, results, info ) {
*    // Check for rate limit information...
*    if ( info ) {
*        console.error( 'Limit: %d', info.limit );
*        console.error( 'Remaining: %d', info.remaining );
*        console.error( 'Reset: %s', (new Date( info.reset*1000 )).toISOString() );
*    }
*    if ( error ) {
*        throw new Error( error.message );
*    }
*    console.log( JSON.stringify( results ) );
*    /* returns
*        {
*            "id": 1,
*            "url":"https://api.github.com/authorizations/1",
*            "app": {
*                "name":"beep",
*                "url":"https://developer.github.com/v3/oauth_authorizations/",
*                "client_id":"00000000000000000000"
*            },
*            "token": "abcdefgh12345678",
*            "hashed_token": "25f94a2a5c7fbaf499c665bc73d67c1c87e496da8985131633ee0a95819db2e8",
*            "token_last_eight": "12345678",
*            "note": "beep",
*            "note_url": null,
*            "created_at": "2016-03-05T06:34:01Z",
*            "updated_at": "2016-03-05T06:34:01Z",
*            "scopes": [
*                "public_repo",
*                "read:org"
*            ],
*            "fingerprint": null
*        }
*    *\/
* }
*/

// MODULES //

var main = require( './main.js' );


// EXPORTS //

module.exports = main;
