'use strict';

// MODULES //

var uppercase = require( '@stdlib/string/base/uppercase' );


// MAIN //

/**
* Returns a resource path.
*
* @private
* @param {string} pathname - resource pathname
* @param {string} type - dependency type
* @returns {string} resource path
*/
function path( pathname, type ) {
	return pathname + '?dependent_type=' + uppercase( type );
}


// EXPORTS //

module.exports = path;
