'use strict';

// MODULES //

var logger = require( 'debug' );


// VARIABLES //

var debug = logger( 'github-dispatch-workflow:data' );


// MAIN //

/**
* Extracts data to be posted to a remote endpoint.
*
* @private
* @param {Object} opts - options
* @param {string} [opts.ref='main'] - git reference for the workflow (branch or tag name)
* @param {Object} [opts.inputs] - input key-value pairs overriding the defaults specified in the workflow file
* @returns {string} data to post
*/
function data( opts ) {
	var out = {
		'ref': 'main'
	};

	if ( opts.ref ) {
		out.ref = opts.ref;
	}
	debug( 'ref: %s', opts.ref );
	if ( opts.inputs ) {
		out.inputs = opts.inputs;
		debug( 'inputs: %s', JSON.stringify( opts.inputs ) );
	}
	return JSON.stringify( out );
}


// EXPORTS //

module.exports = data;
