'use strict';

// MODULES //

var tape = require( 'tape' );
var data = require( './../lib/data.js' );


// FUNCTIONS //

function setup() {
	return {
		'ref': 'master'
	};
}


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof data, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns a string', function test( t ) {
	t.equal( typeof data( setup() ), 'string', 'returns a string' );
	t.end();
});

tape( 'the function sets the `ref` field', function test( t ) {
	var opts;
	var out;

	opts = setup();
	out = data( opts );
	out = JSON.parse( out );

	t.deepEqual( out.ref, opts.ref, 'sets the `ref` field' );
	t.end();
});

tape( 'the function sets the `inputs` field', function test( t ) {
	var opts;
	var out;

	opts = setup();
	opts.inputs = {
		'x': 3,
		'y': 2
	};

	out = data( opts );
	out = JSON.parse( out );

	t.deepEqual( out.inputs, opts.inputs, 'sets the `inputs` field' );
	t.end();
});
