tape( 'the function returns `null` if all options are valid', function test( t ) {
	var options;
	var opts;
	var err;

	opts = {};
	options = {
		'protocol': 'http',
		'hostname': 'beep.com',
		'port': 8080,
		'ref': 'main',
		'token': 'abcdefg',
		'useragent': 'beeper-booper'
	};

	err = validate( opts, options );
	t.strictEqual( err, null, 'returns null' );
	t.deepEqual( opts, options, 'sets options' );

	t.end();
});

tape( 'the function ignores unrecognized options', function test( t ) {
	var expected;
	var options;
	var opts;
	var err;

	opts = {};
	options = {
		'token': '1234',
		'beep': 'boop',
		'a': 5,
		'b': null,
		'c': 'woot'
	};
	expected = {
		'token': '1234'
	};

	err = validate( opts, options );
	t.strictEqual( err, null, 'returns null' );
	t.deepEqual( opts, expected, 'ignores unrecognized options' );

	t.end();
});
