'use strict';

/**
* Fetch a file from one or more public GitHub repositories.
*
* @module @stdlib/_tools/github/fetch-file
*
* @example
* var fetchFile = require( '@stdlib/_tools/github/fetch-file' );
*
* // List of repository slugs (username|org/repo):
* var repos = [
*     'kgryte/utils-copy',
*     'dstructs/matrix',
*     'math-io/gamma',
*     'unknown_user/repo'
* ];
*
* // Fetch a top-level `README.md` file from each repo:
* fetchFile( 'README.md', repos, clbk );
*
* function clbk( error, results ) {
*     if ( error ) {
*         throw new Error( error.message );
*     }
*     console.dir( results );
*     /* =>
*         {
*             'meta': {
*                 'total': 4,
*                 'success': 3,
*                 'failure': 1
*             },
*             'data': {
*                 'kgryte/utils-copy': '...',
*                 'dstructs/matrix': '...',
*                'math-io/gamma': '...'
*             },
*             'failures': {
*                 'unknown_user/repo': 'Not Found'
*             }
*         }
*     *\/
* }
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-nonenumerable-read-only-property' );
var main = require( './main.js' );
var factory = require( './factory.js' );


// MAIN //

setReadOnly( main, 'factory', factory );


// EXPORTS //

module.exports = main;
