'use strict';

/**
* Shuffle a data array based on a sort vector.
*
* @param {ObjectArray} data - data to shuffle
* @param {ArrayArray} sorted - sort vector, where each element is a two-element tuple in which the first tuple element is the value's original index
* @returns {ObjectArray} shuffled data array
*/
function shuffle( data, sorted ) {
	var out;
	var i;
	out = new Array( data.length );
	for ( i = 0; i < data.length; i++ ) {
		out[ i ] = data[ sorted[i][0] ];
	}
	return out;
}


// EXPORTS //

module.exports = shuffle;
