tape( 'function options are optional', function test( t ) {
	var expected;
	var rank;

	rank = proxyquire( './../lib/main.js', {
		'./factory.js': factory
	});

	expected = results;

	rank( setup(), done );

	function factory( opts, clbk ) {
		return function rank() {
			setTimeout( onTimeout, 0 );
			function onTimeout() {
				clbk( null, results, info );
			}
		};
	}

	function done( error, data ) {
		t.equal( error, null, 'does not return an error' );
		t.deepEqual( data, expected, 'deep equal' );
		t.end();
	}
});
