tape( 'function supports requesting user details without authentication (no token)', function test( t ) {
	var expected;
	var userinfo;
	var opts;

	userinfo = proxyquire( './../lib/main.js', {
		'./factory.js': factory
	});

	expected = results;

	opts = getOpts();
	delete opts.token;

	userinfo( opts, done );

	function factory( opts, clbk ) {
		return function userinfo() {
			setTimeout( onTimeout, 0 );
			function onTimeout() {
				clbk( null, results, info );
			}
		};
	}

	function done( error, results ) {
		t.equal( error, null, 'does not return an error' );
		t.deepEqual( results, expected, 'deep equal' );
		t.end();
	}
});
