'use strict';

/**
* Get a user's rate limit status.
*
* @module @stdlib/_tools/github/user-rate-limit
*
* @example
* var ratelimit = require( '@stdlib/_tools/github/user-rate-limit' );
*
* var opts = {
*     'token': 'tkjorjk34ek3nj4!'
* };
*
* ratelimit( opts, clbk );
*
* function clbk( error, status ) {
*     if ( error ) {
*         throw new Error( error.message );
*     }
*     console.dir( status );
*     /*
*         {
*             "core": {
*                 "limit": 5000,
*                 "remaining": 4999,
*                 "reset": 1372700873 // UTC seconds
*             },
*             "search": {
*                 "limit": 30,
*                 "remaining": 18,
*                 "reset": 1372697452 // UTC seconds
*             }
*         }
*     *\/
* }
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-nonenumerable-read-only-property' );
var main = require( './main.js' );
var factory = require( './factory.js' );


// MAIN //

setReadOnly( main, 'factory', factory );


// EXPORTS //

module.exports = main;
