'use strict';

// MODULES //

var isFunction = require( '@stdlib/assert/is-function' );
var format = require( '@stdlib/string/format' );
var merge = require( '@stdlib/utils/merge' );
var request = require( '@stdlib/_tools/github/get' ).factory;
var validate = require( './validate.js' );
var defaults = require( './defaults.json' );


// MAIN //

/**
* Returns a function for fetching all workflow runs for a repository.
*
* @param {Object} options - function options
* @param {string} options.slug - GitHub slug (:owner/:repo)
* @param {string} [options.token] - GitHub access token
* @param {string} [options.useragent] - user agent string
* @param {string} [options.actor] - only return workflow runs by the specified user
* @param {string} [options.branch] - only return workflow runs associated with a branch
* @param {string} [options.event] - only return workflow runs triggered by the specified event
* @param {string} [options.status] - only return workflow runs with the specified check run status or conclusion
* @param {Function} clbk - callback to invoke upon query completion
* @throws {TypeError} callback argument must be a function
* @returns {Function} function for getting repository workflow runs
*/
function factory( options, clbk ) {
	var opts;
	var err;
	opts = merge( {}, defaults );
	err = validate( opts, options );
	if ( err ) {
		throw err;
	}
	if ( !isFunction( clbk ) ) {
		throw new TypeError( format( 'invalid argument. Callback argument must be a function. Value: `%s`.', clbk ) );
	}
	opts.pathname += 'repos/'+opts.slug+'/actions/runs';
	if ( opts.actor ) {
		if ( opts.query ) {
			opts.query += '&';
		}
		opts.query += 'actor='+opts.actor;
		delete opts.actor;
	}
	if ( opts.branch ) {
		if ( opts.query ) {
			opts.query += '&';
		}
		opts.query += 'branch='+opts.branch;
		delete opts.branch;
	}
	if ( opts.event ) {
		if ( opts.query ) {
			opts.query += '&';
		}
		opts.query += 'event='+opts.event;
		delete opts.event;
	}
	if ( opts.status ) {
		if ( opts.query ) {
			opts.query += '&';
		}
		opts.query += 'status='+opts.status;
		delete opts.status;
	}
	return request( opts, done );
	/**
	* Callback invoked after resolving resources.
	*
	* @private
	* @param {(Error|null)} error - error object
	* @param {ObjectArray} data - query data
	* @param {Object} info - response info
	* @returns {void}
	*/
	function done( error, data, info ) {
		error = error || null;
		data = data || null;
		info = info || null;
		clbk( error, data, info );
	}
}


// EXPORTS //

module.exports = factory;
