'use strict';

// MODULES //

var factory = require( './factory.js' );


// MAIN //

/**
* Gets all workflow runs for a repository.
*
* @param {Object} opts - function options
* @param {string} opts.slug - GitHub slug (:owner/:repo)
* @param {string} [opts.token] - GitHub access token
* @param {string} [opts.useragent] - user agent string
* @param {string} [opts.actor] - only return workflow runs by the specified user
* @param {string} [opts.branch] - only return workflow runs associated with a branch
* @param {string} [opts.event] - only return workflow runs triggered by the specified event
* @param {string} [opts.status] - only return workflow runs with the specified check run status or conclusion
* @param {Function} clbk - callback to invoke upon query completion
* @returns {void}
*/
function workflowRuns( opts, clbk ) {
	factory( opts, clbk )();
}


// EXPORTS //

module.exports = workflowRuns;
