'use strict';

// MODULES //

var resolve = require( 'path' ).resolve;
var readFile = require( '@stdlib/fs/read-file' ).sync;
var hasOwnProp = require( '@stdlib/assert/has-own-property' );
var type2ext = require( './type2ext.json' );


// VARIABLES //

var fopts = {
	'encoding': 'utf8'
};


// MAIN //

/**
* Returns a file license header.
*
* @private
* @param {string} license - lowercased SPDX license identifier
* @param {string} type - lowercased file type
* @returns {(string|null)} license header or null
*
* @example
* var h = header( 'apache-2.0', 'js' );
*/
function header( license, type ) {
	var fpath;
	var ext;

	// Convert the file type to a canonical file extension:
	if ( !hasOwnProp( type2ext, type ) ) {
		return null;
	}
	ext = type2ext[ type ];

	// Resolve the header file path:
	fpath = resolve( __dirname, '..', 'data', license+'.'+ext+'.txt' );

	// Return the header:
	return readFile( fpath, fopts );
} // end FUNCTION header()


// EXPORTS //

module.exports = header;
