'use strict';

// MODULES //

var isObject = require( '@stdlib/assert/is-plain-object' );
var hasOwnProp = require( '@stdlib/assert/has-own-property' );
var isPositiveInteger = require( '@stdlib/assert/is-positive-integer' ).isPrimitive;
var isString = require( '@stdlib/assert/is-string' ).isPrimitive;
var format = require( '@stdlib/string/format' );


// MAIN //

/**
* Validates function options.
*
* @private
* @param {Object} opts - destination object
* @param {Options} options - function options
* @param {PositiveInteger} [options.year] - copyright year
* @param {string} [options.copyright] - copyright owner
* @returns {(Error|null)} null or an error object
*
* @example
* var opts = {};
* var options = {
*     'year': 2018,
*     'copyright': 'The Stdlib Authors'
* };
* var err = validate( opts, options );
* if ( err ) {
*     throw err;
* }
*/
function validate( opts, options ) {
	if ( !isObject( options ) ) {
		return new TypeError( format( 'invalid argument. Options argument must be an object. Value: `%s`.', options ) );
	}
	if ( hasOwnProp( options, 'year' ) ) {
		opts.year = options.year;
		if ( !isPositiveInteger( opts.year ) ) {
			return new TypeError( format( 'invalid option. `%s` option must be a positive integer. Option: `%s`.', 'year', opts.year ) );
		}
	}
	if ( hasOwnProp( options, 'copyright' ) ) {
		opts.copyright = options.copyright;
		if ( !isString( opts.copyright ) ) {
			return new TypeError( format( 'invalid option. `%s` option must be a string. Option: `%s`.', 'copyright', opts.copyright ) );
		}
	}
	return null;
}


// EXPORTS //

module.exports = validate;
