'use strict';

/**
* Insert a license header into a source code string.
*
* @module @stdlib/_tools/licenses/insert-header
*
* @example
* var licenseHeader = require( '@stdlib/_tools/licenses/header' );
* var insertHeader = require( '@stdlib/_tools/licenses/insert-header' );
*
* var opts = {
*     'year': 2018,
*     'copyright': 'The Stdlib Authors'
* };
*
* var h = licenseHeader( 'Apache-2.0', 'js', opts );
* // returns <string>
*
* var src = [
*     '"use strict";',
*     '',
*     'var x = 3.14;',
*     ''
* ];
*
* var out = insertHeader( src.join( '\n' ), h );
* // returns <string>
*/

// MODULES //

var main = require( './main.js' );


// EXPORTS //

module.exports = main;
